import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import './index.scss';
import LogoNoName from '../../assets/images/logo-no-name.png'
import AnimatedLetters from '../../components/AnimatedLetters/index';

export default function Home() {
  const [ letterClass, setLetterClass] = useState('text-animate');
  const nameArray = ['A', 'n', 't', 'h', 'o', 'n', 'y'];
  const jobArray = ['D', 'e', 's', 'e', 'n', 'v', 'o', 'l', 'v', 'e', 'd', 'o', 'r', ' ', 'w', 'e', 'b'];
  const welcome = ['B', 'e', 'm', ' ', 'v', 'i', 'n', 'd', 'o', '!'];
  const iAm = ['E', 'u', ' ', 's', 'o', 'u'];

  useEffect(() => {
    const timeout = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 4800);
    return () => {
      timeout();
    }
  }, [])
  // console.log('renderizou', letterClass);
  return (
    <div className="container home-page">
      <div className="text-zone">
      <h1>
        {/* <AnimatedLetters strArray={ welcome } letterClass={ letterClass } idx={ 11 }/>
        <br />
        <AnimatedLetters strArray={ iAm } letterClass={ letterClass } idx={ 19 }/> */}

        <span className={ letterClass }>B</span>
        <span className={ `${letterClass} _12` }>e</span>
        <span className={ `${letterClass} _13` }>m</span>
        &nbsp;
        <span className={ `${letterClass} _14` }>v</span>
        <span className={ `${letterClass} _15` }>i</span>
        <span className={ `${letterClass} _16` }>n</span>
        <span className={ `${letterClass} _17` }>d</span>
        <span className={ `${letterClass} _18` }>o!</span>
        <br />
        <span className={ `${letterClass} _19` }>E</span>
        <span className={ `${letterClass} _20` }>u</span>
        &nbsp;
        <span className={ `${letterClass} _21` }>s</span>
        <span className={ `${letterClass} _22` }>o</span>
        <span className={ `${letterClass} _23` }>u</span>
        &nbsp;

      {/* <img src={ LogoNoName } alt="anthony logo" /> */}

      <AnimatedLetters
      letterClass={ letterClass }
      strArray={ nameArray }
      idx={ 24 }
    />
      <br />
      <AnimatedLetters
      letterClass={ letterClass }
      strArray={ jobArray }
      idx={ 31 }
    />
      </h1>
      <h2>Full stack developer</h2>
      <Link to="/contact" className="flat-button">Contact me</Link>
      </div>
    </div>
  )
}
