export default function SpanLetterCreate({ array, letterClass, initialNum }) {
  let counter = initialNum - 1;
  return (
    array.map((letter) => {
      if (letter === ' ') { return ' ' }
      counter += 1;
      return (
        <span className={ `${letterClass} _${counter}` }>{ letter }</span>
      )
    })
  )
}
