import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import './index.scss';

import AnimatedLetters from '../../components/AnimatedLetters/index';
import AnimatedIcons from '../../components/AnimatedIcons/index';

export default function Home() {
  const [ letterClass, setLetterClass] = useState('text-animate');
  const nameArray = ['A', 'n', 't', 'h', 'o', 'n', 'y'];
  const jobArray = ['D', 'e', 's', 'e', 'n', 'v', 'o', 'l', 'v', 'e', 'd', 'o', 'r', ' ', 'w', 'e', 'b'];
  const welcome = ['B', 'e', 'm', ' ', 'v', 'i', 'n', 'd', 'o', '!'];
  const iAm = ['E', 'u', ' ', 's', 'o', 'u'];

  useEffect(() => {
    setTimeout(() => {
      setLetterClass('text-animate-houver')
    }, 2000);
    return () => {
      clearInterval();
    }
  }, [])

  return (
    <div className="container home-page">
      <div className="text-zone">
        <h1>
          <AnimatedLetters
            strArray={ welcome }
            letterClass={ letterClass }
            idx={ 12 }
          />
          <br />
          <AnimatedLetters
            strArray={ iAm }
            letterClass={ letterClass }
            idx={ 19 }
          />
          &nbsp;
          <AnimatedLetters
            letterClass={ letterClass }
            strArray={ nameArray }
            idx={ 24 }
          />
          <br />
          <AnimatedLetters
            letterClass={ letterClass }
            strArray={ jobArray }
            idx={ 31 }
          />
        </h1>
        <h2>Full stack developer</h2>
        <Link to="/contact" className="flat-button">Contact me</Link>
      </div>
      <AnimatedIcons />
    </div>
  )
}
