import { useContext, useEffect } from 'react';

import { Loader } from 'react-loaders';
import './index.scss';
import AnimatedLetters from '../../components/AnimatedLetters/index';

import PortfolioContext from '../../context/PortfolioContext';

export default function Contact() {
  const { letterClass, setLetterClass } = useContext(PortfolioContext);
  const contactMe = 'Contato';

  useEffect(() => {
    setTimeout(() => {
      setLetterClass('text-animate-houver');
    }, 3000);
    return () => setLetterClass('text-animate');
  }, [setLetterClass]);

  return (
    <>
      <div className="container contact-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters str={ contactMe } idx={ 15 } letterClass= { letterClass } />
          </h1>
        </div>
      </div>
      <div className="contact-form">
        <form onSubmit={ () => {} }>
          <ul>
            <li className="half">
              <input
                type="text"
                name="name"
                placeholder="Nome"
                required
              />
            </li>
            <li className="half">
              <input
                type="email"
                name="email"
                placeholder="Email"
                required
              />
            </li>
            <li className="half">
              <input
                type="text"
                name="subject"
                placeholder="Assunto"
                required
            />
            </li>
          </ul>
        </form>
      </div>
      <Loader type="ball-clip-rotate"/>
    </>
  );
}
